import React from 'react';


class MovieList extends React.Component {

    render() {
        return (
            <div className="row">
                {this.props.movies.map((movie) => (
                    <div className="col-lg-4">
                        <div className="card mb-4 shadow-sm">
                            <img src={movie.imageURL} className="card-img-top" alt="Movie image" />
                            <div className="card-body">
                                <h5 className="card-title">{movie.name}</h5>
                                <p className="card-text">Sample Movie Description</p>
                                <div className="d-flex justify-content-between align-items-center">
                                    <button className="btn btn-md btn-outline-danger" type="button">Delete</button>
                                    <h2><span className="badge bg-info">9.0</span></h2>
                                </div>
                            </div>
                        </div>
                    </div>

                ))}

            </div>
        )
    }
}

export default MovieList
