import React from 'react';
import { Link } from 'react-router-dom';
import '../css/style.css'


const MovieList = (props) => {

    const truncateOverview = (string, maxLength) => {
        if (!string) return null;
        if (string.length <= maxLength) return string;
        return `${string.substring(0, maxLength)} ...`;
    }


    return (
        <div className="row">
              {props.movies.map((movie, i) => (
               <div class="example-2 card">
                <div class="wrapper w-100">
                    <div className="header">
                    <h2><span className="badge bg-info">{movie.rating}</span></h2>
                    </div>
                    <div class="data">
                        <div class="content">
                            <h1 class="title"><a href="#">{movie.name}</a></h1>
                            <p class="text">{truncateOverview(movie.overview, 120)}</p>
                            <div className="d-flex justify-content-between align-items-center">
                                <button className="btn btn-md btn-outline-danger" type="button" onClick={(event) => props.deleteMovieProp(movie)}>Delete</button>

                                <Link type="button"
                                    className="btn btn-md btn-outline-primary"
                                    to={`edit/${movie.id}`}
                                >Edit </Link>


                               
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            ))}

        </div>

    )

}

export default MovieList
