        if (!string) return null;
        if (string.length <= maxLength) return string;
        return `${string.substring(0, maxLength)} ...`;
    }


    return (
        <div className="row">
            <div class="example-2 card">
                <div class="wrapper">
                    <div class="data">
                        <div class="content">
                            <h1 class="title"><a href="#">{movie.name}</a></h1>
                            <p class="text">{truncateOverview(movie.overview, 120)}</p>
                            <a href="#" class="button">Read more</a>
                        </div>
                    </div>
                </div>
            </div>

